+apply plugin: 'com.android.library'
+apply plugin: 'com.github.dcendents.android-maven'
+apply plugin: 'jacoco'
+
+install {
+    repositories.mavenInstaller {
+        pom {
+            project {
+                packaging 'aar'
+                name 'Agera'
+                url gradle.url
+                licenses {
+                    license {
+                        name gradle.licenseName
+                        url gradle.licenseUrl
+                    }
+                }
+                scm {
+                    url gradle.url
+                    connection gradle.gitUrl
+                }
+            }
+        }
+    }
+}

