+    @Override
+    public boolean apply(@NonNull final T value) {
+      for (final Predicate<T> predicate : predicates) {
+        if (predicate.apply(value) == definingResult) {
+          return definingResult;
+        }
+      }
+      return !definingResult;
+    }
+  }
