+    @NonNull
+    @Override
+    public List<C> merge(@NonNull final List<A> firstList,
+        @NonNull final List<B> secondList) {
+      final List<C> result = new ArrayList<>(firstList.size() + secondList.size());
+      result.addAll(firstList);
+      result.addAll(secondList);
+      return result;
+    }
+  }
