+package com.google.android.agera.net.test.matchers;
+
+import org.hamcrest.Description;
+import org.hamcrest.Factory;
+import org.hamcrest.Matcher;
+import org.hamcrest.TypeSafeMatcher;
+
+public final class HasHashCodeOf extends TypeSafeMatcher<Object> {
+  private final Object object;
+
+  private HasHashCodeOf(final Object object) {
+    this.object = object;
+  }
+
+  @Override
+  protected boolean matchesSafely(final Object item) {
+    return item.hashCode() == object.hashCode();
+  }
+
+  @Override
+  public void describeTo(final Description description) {
+    description.appendText("same hashcode as ");
+    description.appendValue(object);
+  }
+
+  @Factory
+  public static Matcher<Object> hasHashCodeOf(final Object object) {
+    return new HasHashCodeOf(object);
+  }
+}

